FiveDayReturns = []
for stock in stocks:
    try:
        val = DataReader(stock,  'yahoo', datetime(2017, 3, 29), datetime(2017, 4, 4))
        FiveDayReturns.append((val['Adj Close'][4] / val['Adj Close'][0]) -1)
    except:
        FiveDayReturns.append(0)

#build 2d array
arr=[]
for r in FiveDayReturns:
    subarr = []
    for s in FiveDayReturns:
        #positive 5 day return
        if r>0:
            if s>=r:
                subarr.append(1)
            elif s<r and s>-r:
                subarr.append(0)
            else:
                subarr.append(-1)
        #negative 5 day return
        elif r<0:
            if s<=r:
                subarr.append(1)
            elif s>r and s<-r:
                subarr.append(0)
            else:
                subarr.append(-1)
        else:
            subarr.append(0)
    arr.append(subarr)

a = np.array(arr)
sparse_arr = csr_matrix(a)

